import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginSignup from './components/LoginSignup';
import Dashboard from './components/Dashboard';
import AddPaper from './components/AddPaper';
import EditPaper from './components/EditPaper';
import ForgotPassword from './components/ForgotPassword';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [papers, setPapers] = useState([
    {
      id: 1,
      title: "A Comprehensive Review of Deep Learning Techniques for Image Recognition",
      description: "This paper provides an in-depth analysis of various deep learning models used for image recognition tasks, highlighting their strengths and weaknesses.",
      dateAdded: "Jan 15, 2024",
      authors: ["John Smith", "Jane Doe"],
      abstract: "This paper provides an in-depth analysis of various deep learning models used for image recognition tasks, highlighting their strengths and weaknesses.",
      keywords: ["deep learning", "image recognition", "neural networks"],
      references: ["Smith, J. (2023). Deep Learning Fundamentals", "Doe, J. (2023). Image Processing Techniques"]
    },
    {
      id: 2,
      title: "Advancements in Natural Language Processing: A Survey of Recent Methods",
      description: "This survey explores the latest techniques in NLP, including transformer models and their applications in text analysis and generation.",
      dateAdded: "Feb 02, 2024",
      authors: ["Alice Johnson", "Bob Wilson"],
      abstract: "This survey explores the latest techniques in NLP, including transformer models and their applications in text analysis and generation.",
      keywords: ["natural language processing", "transformers", "text analysis"],
      references: ["Johnson, A. (2023). NLP Advances", "Wilson, B. (2023). Transformer Architecture"]
    },
    {
      id: 3,
      title: "The Role of Blockchain in Securing Digital Transactions",
      description: "This article examines the use of blockchain technology to enhance the security and integrity of digital transactions.",
      dateAdded: "Mar 21, 2024",
      authors: ["Chris Brown", "Diana White"],
      abstract: "This article examines the use of blockchain technology to enhance the security and integrity of digital transactions.",
      keywords: ["blockchain", "digital security", "transactions"],
      references: ["Brown, C. (2023). Blockchain Security", "White, D. (2023). Digital Transaction Methods"]
    }
  ]);

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
  };

  const addPaper = (newPaper) => {
    const paper = {
      id: papers.length + 1,
      ...newPaper,
      dateAdded: new Date().toLocaleDateString('en-US', { 
        year: 'numeric', 
        month: 'short', 
        day: '2-digit' 
      })
    };
    setPapers([...papers, paper]);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route 
            path="/login" 
            element={
              isAuthenticated ? 
              <Navigate to="/dashboard" replace /> : 
              <LoginSignup onLogin={handleLogin} />
            } 
          />
          <Route 
            path="/dashboard" 
            element={
              isAuthenticated ? 
              <Dashboard papers={papers} onLogout={handleLogout} /> : 
              <Navigate to="/login" replace />
            } 
          />
          <Route 
            path="/add-paper" 
            element={
              isAuthenticated ? 
              <AddPaper onAddPaper={addPaper} /> : 
              <Navigate to="/login" replace />
            } 
          />
          <Route 
            path="/forgot-password" 
            element={
              isAuthenticated ? 
              <Navigate to="/dashboard" replace /> : 
              <ForgotPassword />
            } 
          />
          <Route path="/" element={<Navigate to="/login" replace />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
