import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './EditPaper.css';

const EditPaper = ({ papers, onUpdatePaper }) => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [formData, setFormData] = useState({
    title: '',
    abstract: '',
    keywords: '',
    references: '',
    authors: ['']
  });

  // Find the paper to edit
  useEffect(() => {
    const paperToEdit = papers.find(paper => paper.id === parseInt(id));
    if (paperToEdit) {
      setFormData({
        title: paperToEdit.title || '',
        abstract: paperToEdit.abstract || '',
        keywords: Array.isArray(paperToEdit.keywords) ? paperToEdit.keywords.join(', ') : paperToEdit.keywords || '',
        references: Array.isArray(paperToEdit.references) ? paperToEdit.references.join('\n') : paperToEdit.references || '',
        authors: paperToEdit.authors && paperToEdit.authors.length > 0 ? paperToEdit.authors : ['']
      });
    } else {
      // Paper not found, redirect to dashboard
      navigate('/dashboard');
    }
  }, [id, papers, navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAuthorChange = (index, value) => {
    const newAuthors = [...formData.authors];
    newAuthors[index] = value;
    setFormData(prev => ({
      ...prev,
      authors: newAuthors
    }));
  };

  const addAuthor = () => {
    setFormData(prev => ({
      ...prev,
      authors: [...prev.authors, '']
    }));
  };

  const removeAuthor = (index) => {
    if (formData.authors.length > 1) {
      const newAuthors = formData.authors.filter((_, i) => i !== index);
      setFormData(prev => ({
        ...prev,
        authors: newAuthors
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Validation
    if (!formData.title.trim()) {
      alert('Please enter a paper title');
      return;
    }

    if (!formData.abstract.trim()) {
      alert('Please enter an abstract');
      return;
    }

    if (formData.authors.some(author => !author.trim())) {
      alert('Please fill in all author names or remove empty author fields');
      return;
    }

    // Prepare updated data
    const updatedPaper = {
      id: parseInt(id),
      title: formData.title.trim(),
      abstract: formData.abstract.trim(),
      description: formData.abstract.trim().substring(0, 150) + '...',
      keywords: formData.keywords.split(',').map(k => k.trim()).filter(k => k),
      references: formData.references.split('\n').filter(r => r.trim()),
      authors: formData.authors.filter(author => author.trim())
    };

    onUpdatePaper(updatedPaper);
    navigate('/dashboard');
  };

  const handleCancel = () => {
    navigate('/dashboard');
  };

  return (
    <div className="edit-paper-container">
      <header className="edit-paper-header">
        <div className="header-left">
          <button onClick={handleCancel} className="back-button">
            ← Back to Dashboard
          </button>
          <h1>Edit Paper</h1>
        </div>
      </header>

      <main className="edit-paper-main">
        <form onSubmit={handleSubmit} className="edit-paper-form">
          <div className="form-columns">
            <div className="form-column">
              <div className="form-group">
                <label htmlFor="title">Title *</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  placeholder="Enter paper title"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="abstract">Abstract *</label>
                <textarea
                  id="abstract"
                  name="abstract"
                  value={formData.abstract}
                  onChange={handleInputChange}
                  placeholder="Enter paper abstract"
                  rows="6"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="keywords">Keywords</label>
                <input
                  type="text"
                  id="keywords"
                  name="keywords"
                  value={formData.keywords}
                  onChange={handleInputChange}
                  placeholder="Enter keywords, separated by commas"
                />
                <small className="field-hint">Separate multiple keywords with commas</small>
              </div>
            </div>

            <div className="form-column">
              <div className="form-group">
                <label>Authors *</label>
                <div className="authors-section">
                  {formData.authors.map((author, index) => (
                    <div key={index} className="author-input-group">
                      <input
                        type="text"
                        value={author}
                        onChange={(e) => handleAuthorChange(index, e.target.value)}
                        placeholder={`Author ${index + 1} name`}
                        required
                      />
                      {formData.authors.length > 1 && (
                        <button
                          type="button"
                          onClick={() => removeAuthor(index)}
                          className="remove-author-button"
                        >
                          ×
                        </button>
                      )}
                    </div>
                  ))}
                  <button
                    type="button"
                    onClick={addAuthor}
                    className="add-author-button"
                  >
                    + Add Author
                  </button>
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="references">References</label>
                <textarea
                  id="references"
                  name="references"
                  value={formData.references}
                  onChange={handleInputChange}
                  placeholder="Enter references, one per line"
                  rows="6"
                />
                <small className="field-hint">Enter each reference on a new line</small>
              </div>
            </div>
          </div>

          <div className="form-actions">
            <button type="button" onClick={handleCancel} className="cancel-button">
              Cancel
            </button>
            <button type="submit" className="submit-button">
              Update Paper
            </button>
          </div>
        </form>
      </main>
    </div>
  );
};

export default EditPaper;
